import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import award from "../assets/img/award.jpg"; // Replace with an appropriate image for achievements
import trophy from "../assets/img/trophy.jpg"; // Replace with another image for achievements
import colorSharp from "../assets/img/color-sharp.png";
import './App.css';

export const Achievement = () => {
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 3
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 2
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  return (
    <section className="achievement" id="achievement">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="achievement-bx wow zoomIn">
              <h2>Achievements</h2>
              <p>These are some of my achievements that highlight my journey and contributions in various fields.</p>
              <Carousel responsive={responsive} infinite={true} className="owl-carousel owl-theme achievement-slider">
                <div className="item">
                  <img src={award} alt="Award" />
                  <h5>Google Girl Hackathon Finalist</h5>
                  <p>Finalist in the Google Girl Hackathon 2024 for developing a model for semantic segmentation of flood images.</p>
                </div>
                <div className="item">
                  <img src={trophy} alt="Trophy" />
                  <h5>ICPC Asia-Kanpur Prelims</h5>
                  <p>Achieved a global rank of 1111 in the ICPC Asia-Kanpur Prelims Contest.</p>
                </div>
                <div className="item">
                  <img src={award} alt="Award" />
                  <h5>Scholarships & Recognitions</h5>
                  <p>Received multiple scholarships and recognitions for academic excellence and tech contributions.</p>
                </div>
              </Carousel>
            </div>
          </div>
        </div>
      </div>
      <img className="background-image-left" src={colorSharp} alt="Background" />
    </section>
  );
}
