import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import '../App.css'; // Assuming you have your general styles here

const Responsibilities = () => {
  const responsibilities = [
    { title: 'Anchor - Google Developers Group Raipur', logo: 'path/to/gdg-logo.png', description: 'Conduct workshops and meetups to encourage developer engagement.' },
    { title: 'Core Member - AI and ML Club at IIITNR', logo: 'path/to/ai-ml-logo.png', description: 'Collaborate on projects and host events to promote AI and ML learning.' },
    { title: 'Mentor - Codess Community', logo: 'path/to/codess-logo.png', description: 'Guide and support mentees in their learning journeys.' },
    { title: 'Event Organizer - Tech Fest', logo: 'path/to/techfest-logo.png', description: 'Plan and execute tech-related events to foster community interaction.' },
  ];

  const responsive = {
    superLarge: {
      // for very large screens
      breakpoint: { max: 4000, min: 3000 },
      items: 4,
    },
    large: {
      // for large screens
      breakpoint: { max: 3000, min: 1024 },
      items: 3,
    },
    medium: {
      // for medium screens
      breakpoint: { max: 1024, min: 464 },
      items: 2,
    },
    small: {
      // for small screens
      breakpoint: { max: 464, min: 0 },
      items: 1,
    },
  };

  return (
    <div className="responsibilities-section">
      <h2 className="section-title">Responsibilities</h2>
      <Carousel responsive={responsive} infinite={true} className="responsibilities-carousel">
        {responsibilities.map((item, index) => (
          <div key={index} className="responsibility-card">
            <img src={item.logo} alt={item.title} className="responsibility-logo" />
            <h3>{item.title}</h3>
            <p>{item.description}</p>
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default Responsibilities;
