// Achievements.js
import React from 'react';
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import './App.css'; // Import for additional styles

const Achievements = () => {
  const achievements = [
    { title: 'Google Girl Hackathon Finalist', description: 'Finalist in Google Girl Hackathon 2024 for hazard assessment tool using satellite images.' },
    { title: 'ICPC Rank 1111', description: 'Ranked 1111 in ICPC Asia-Kanpur Prelims Contest.' },
    { title: 'GirlScript Summer of Code', description: 'Contributed to improving medicinal data interpretation and visualization.' },
    { title: 'Scholarships and Recognitions', description: 'Received various scholarships and recognitions for academic excellence and contributions.' },
  ];

  const responsive = {
    superLargeDesktop: { breakpoint: { max: 4000, min: 1024 }, items: 3 },
    desktop: { breakpoint: { max: 1024, min: 768 }, items: 2 },
    tablet: { breakpoint: { max: 768, min: 464 }, items: 1 },
    mobile: { breakpoint: { max: 464, min: 0 }, items: 1 }
  };

  return (
    <div className="achievements-section">
      <h2 className="section-title">Achievements</h2>
      <Carousel responsive={responsive} infinite={true} autoPlay={true} autoPlaySpeed={3000}>
        {achievements.map((achievement, index) => (
          <div key={index} className="achievement-card">
            <h3>{achievement.title}</h3>
            <p>{achievement.description}</p>
          </div>
        ))}
      </Carousel>
    </div>
  );
};

export default Achievements;
