import diploma from "../assets/img/diploma.svg"; // Add an appropriate diploma or education-related image
import graduation from "../assets/img/graduation.svg"; // Add a graduation cap image
import Carousel from 'react-multi-carousel';
import 'react-multi-carousel/lib/styles.css';
import colorSharp from "../assets/img/color-sharp.png";

export const Education = () => {
  const responsive = {
    superLargeDesktop: {
      breakpoint: { max: 4000, min: 3000 },
      items: 3
    },
    desktop: {
      breakpoint: { max: 3000, min: 1024 },
      items: 2
    },
    tablet: {
      breakpoint: { max: 1024, min: 464 },
      items: 1
    },
    mobile: {
      breakpoint: { max: 464, min: 0 },
      items: 1
    }
  };

  return (
    <section className="education" id="education">
      <div className="container">
        <div className="row">
          <div className="col-12">
            <div className="education-bx wow zoomIn">
              <h2>Education</h2>
              <p>Here are some of my educational qualifications that have shaped my journey.<br /> I am continuously learning and growing in my field.</p>
              <Carousel responsive={responsive} infinite={true} className="owl-carousel owl-theme education-slider">
                <div className="item">
                  <img src={diploma} alt="Diploma" />
                  <h5>Bachelor of Technology</h5>
                  <p>International Institute of Information Technology, Naya Raipur</p>
                  <span>2022 - 2026</span>
                </div>
                <div className="item">
                  <img src={graduation} alt="Graduation" />
                  <h5>Higher Secondary School(10th,12th)</h5>
                  <p>Delhi Public School,NT</p>
                  <span>2019 - 2021</span>
                </div>
              </Carousel>
            </div>
          </div>
        </div>
      </div>
      <img className="background-image-left" src={colorSharp} alt="Image" />
    </section>
  );
}
