import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import projImg1 from "../assets/img/project-img1.png";
import projImg2 from "../assets/img/project-img2.png";
import projImg3 from "../assets/img/project-img3.png";
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {
  const projects = [
    {
      title: "Flood Management System",
      description: "Python, Keras, OpenCV, Streamlit, Google Map API",
      imgUrl: projImg1,
      githubLink: "https://github.com/yourusername/flood-management-system",
    },
    {
      title: "College ERP",
      description: "Tech-Stack: Python, Django, PostgreSQL, Bootstrap, AWS",
      imgUrl: projImg1,
      githubLink: "https://github.com/yourusername/college-erp",
    },
    {
      title: "Morphological Analyser",
      description: "Python, NLTK, Regular Expressions",
      imgUrl: projImg2,
      githubLink: "https://github.com/yourusername/morphological-analyser",
    },
    {
      title: "Hex Game",
      description: "Python, Jupyter Notebook, HTML, Vs Code, Lvim",
      imgUrl: projImg3,
      githubLink: "https://github.com/yourusername/hex-game",
    },
    {
      title: "Algorithm Visualizer",
      description: "Python, Gemini API, Matplotlib, Node.js",
      imgUrl: projImg2,
      githubLink: "https://github.com/yourusername/algorithm-visualizer",
    },
  ];

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) =>
              <div className={isVisible ? "animate__animated animate__fadeIn": ""}>
                <h2>Projects</h2>
                <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</p>
                <Tab.Container id="projects-tabs" defaultActiveKey="first">
                  <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                    <Nav.Item>
                      <Nav.Link eventKey="first">Tab 1</Nav.Link>
                    </Nav.Item>
                    <Nav.Item>
                      <Nav.Link eventKey="second">Tab 2</Nav.Link>
                    </Nav.Item>
                  </Nav>
                  <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                    <Tab.Pane eventKey="first">
                      <Row>
                        {
                          projects.slice(0, 3).map((project, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...project}
                                />
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                    <Tab.Pane eventKey="second">
                      <Row>
                        {
                          projects.slice(3).map((project, index) => {
                            return (
                              <ProjectCard
                                key={index}
                                {...project}
                                />
                            )
                          })
                        }
                      </Row>
                    </Tab.Pane>
                  </Tab.Content>
                </Tab.Container>
              </div>}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2}></img>
    </section>
  )
}
