import { Container, Row, Col, Tab, Nav } from "react-bootstrap";
import { ProjectCard } from "./ProjectCard";
import { FaGithub } from 'react-icons/fa'; // Importing GitHub icon
import colorSharp2 from "../assets/img/color-sharp2.png";
import 'animate.css';
import TrackVisibility from 'react-on-screen';

export const Projects = () => {

  const projects = [
    {
      title: "Flood Management System",
      description: "Tech Stack: Python, Keras, OpenCV, Streamlit, Google Map API",
      githubLink: "https://github.com/yourusername/flood-management-system", // Add GitHub link
    },
    {
      title: "College ERP",
      description: "Tech Stack: Python, Django, PostgreSQL, Bootstrap, AWS",
      githubLink: "https://github.com/yourusername/college-erp", // Add GitHub link
    },
    {
      title: "Morphological Analyser",
      description: "Tech Stack: Python, NLTK, Regular Expressions",
      githubLink: "https://github.com/yourusername/morphological-analyser", // Add GitHub link
    },
    {
      title: "Hex Game",
      description: "Tech Stack: Python, Jupyter Notebook, HTML, VS Code, Lvim",
      githubLink: "https://github.com/yourusername/hex-game", // Add GitHub link
    },
    {
      title: "Algorithm Visualizer",
      description: "Tech Stack: Python, Gemini API, Matplotlib, Node.js",
      githubLink: "https://github.com/yourusername/algorithm-visualizer", // Add GitHub link
    },
  ];

  return (
    <section className="project" id="projects">
      <Container>
        <Row>
          <Col size={12}>
            <TrackVisibility>
              {({ isVisible }) => (
                <div className={isVisible ? "animate__animated animate__fadeIn" : ""}>
                  <h2>Projects</h2>
                  <p>Lorem Ipsum is simply dummy text of the printing and typesetting industry. Lorem Ipsum has been the industry's standard dummy text ever since the 1500s, when an unknown printer took a galley of type and scrambled it to make a type specimen book.</p>
                  <Tab.Container id="projects-tabs" defaultActiveKey="first">
                    <Nav variant="pills" className="nav-pills mb-5 justify-content-center align-items-center" id="pills-tab">
                      <Nav.Item>
                        <Nav.Link eventKey="first">Tab 1</Nav.Link>
                      </Nav.Item>
                      <Nav.Item>
                        <Nav.Link eventKey="second">Tab 2</Nav.Link>
                      </Nav.Item>
                    </Nav>
                    <Tab.Content id="slideInUp" className={isVisible ? "animate__animated animate__slideInUp" : ""}>
                      <Tab.Pane eventKey="first">
                        <Row>
                          {
                            projects.slice(0, 3).map((project, index) => {
                              return (
                                <ProjectCard
                                  key={index}
                                  title={project.title}
                                  description={project.description}
                                  githubLink={project.githubLink} // Pass the GitHub link
                                />
                              );
                            })
                          }
                        </Row>
                      </Tab.Pane>
                      <Tab.Pane eventKey="second">
                        <Row>
                          {
                            projects.slice(3).map((project, index) => {
                              return (
                                <ProjectCard
                                  key={index}
                                  title={project.title}
                                  description={project.description}
                                  githubLink={project.githubLink} // Pass the GitHub link
                                />
                              );
                            })
                          }
                        </Row>
                      </Tab.Pane>
                    </Tab.Content>
                  </Tab.Container>
                </div>
              )}
            </TrackVisibility>
          </Col>
        </Row>
      </Container>
      <img className="background-image-right" src={colorSharp2} alt="Background" />
    </section>
  );
}
